import './App.css';
import FunctionalComp from './components/FunctionalComponent'
import ClassComp from './components/ClassComponent'
import React from 'react'
//app .js jest głownym komponentem który zostaje wyrenderowany do DOM
//app .js functional komponent
//Default importa mozna nazwać jak się chce
function App() {
    return (
        <div>
            <Parent></Parent>
        </div>      
    );

}


class Parent extends React.Component{
    render(){
        const BOOKS = [
            {
                id:1,
                tytul:"Arthas - Przebudzenie Króla Lisza",
                autor:"Christie golden",
                liczbaStron: 365,
                gatunek: "Fantasy",
                uniwersum: "Warcraft",
                image: "./imgs/arthas.jpg"

            },
            {
                id:2,
                tytul:"Illidan",
                autor:"William King",
                liczbaStron: 414,
                gatunek: "Fantasy",
                uniwersum: "Warcraft",
                image: "./imgs/illidan.jpg"
            },
            {
                id:3,
                tytul:"Fale ciemności",
                autor:"Aaron Rosenberg",
                liczbaStron: 324,
                gatunek: "Fantasy",
                uniwersum: "Warcraft",
                image: "./imgs/fale ciemnosci.jpg"
            }
        ];
        return(
            <Child books={BOOKS}></Child>
            //<ChildImg images
        );
    }
}


class Child extends React.Component{
    state = {
        x: this.props.books[0].id
        // x:'test'
    }
    render(){
        return(
            <div>
                <span>{this.state.x}</span>
                {this.props.books.map(function(Book){
                        return(
                            <div key={Book.id}>
                                <span>Tytuł książki: {Book.tytul} </span>
                                <br></br>
                                <span>Autor: {Book.autor} </span>
                                <br></br>
                                <span>Gatunek: {Book.gatunek} </span>
                                <br></br>
                                <span>Uniwersum: {Book.uniwersum} </span>
                                <br></br>
                                <span>Liczba stron: {Book.liczbaStron} </span>
                                <img src={ require('./imgs/arthas.JPG') } alt="test"></img>
                            </div>

                        )
                    })
                }
            </div>
        )
    }
}

class ChildImg extends React.Component{
    state = {
        x: this.props.books[0].id
        // x:'test'
    }
    render(){
        return(
            <div>
                <span>{this.state.x}</span>
                {this.props.books.map(function(Book){
                        return(
                            <div key={Book.id}>
                                <span>Tytuł książki: {Book.tytul} </span>
                                <br></br>
                                <span>Autor: {Book.autor} </span>
                                <br></br>
                                <span>Gatunek: {Book.gatunek} </span>
                                <br></br>
                                <span>Uniwersum: {Book.uniwersum} </span>
                                <br></br>
                                <span>Liczba stron: {Book.liczbaStron} </span>
                                <button className="btn btn-primary btn-lg">aaaaaa</button>
                            </div>

                        )
                    })
                }
            </div>
        )
    }
}




export default App;
